    const subjectIds = assignments.map(a => a.subjectId);
    if (subjectIds.length === 0) return [];

    return await db.select().from(subjects)
      .where(sql`${subjects.id} IN ${sql.raw(`(${subjectIds.map(id => `'${id}'`).join(',')})`)}`);
