      const monthlySalary = Number(teacher.monthlySalary);

      console.log('[POST /api/teacher-advances] Check:', {
        totalPendingAdvances,
        newAdvanceAmount,
        totalWithNewAdvance,
        monthlySalary,
        exceeds: totalWithNewAdvance > monthlySalary
      });

      if (totalWithNewAdvance > monthlySalary) {
