"use client"

import * as React from "react"
import * as AvatarPrimitive from "@radix-ui/react-avatar"

import { cn } from "@/lib/utils"

const Avatar = React.forwardRef<
  React.ElementRef<typeof AvatarPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof AvatarPrimitive.Root>
>(({ className, ...props }, ref) => (
  <AvatarPrimitive.Root
    ref={ref}
    className={cn(`
      after:content-[''] after:block after:absolute after:inset-0 after:rounded-full after:pointer-events-none after:border after:border-black/10 dark:after:border-white/10
      relative flex h-10 w-10 shrink-0 overflow-hidden rounded-full`,
      className
    )}
    {...props}
  />
))
Avatar.displayName = AvatarPrimitive.Root.displayName

const AvatarImage = React.forwardRef<
  React.ElementRef<typeof AvatarPrimitive.Image>,
  React.ComponentPropsWithoutRef<typeof AvatarPrimitive.Image>
>(({ className, ...props }, ref) => (
  <AvatarPrimitive.Image
    ref={ref}
    className={cn("aspect-square h-full w-full", className)}
    {...props}
  />
))
AvatarImage.displayName = AvatarPrimitive.Image.displayName

const AvatarFallback = React.forwardRef<
  React.ElementRef<typeof AvatarPrimitive.Fallback>,
  React.ComponentPropsWithoutRef<typeof AvatarPrimitive.Fallback>
>(({ className, ...props }, ref) => (
  <AvatarPrimitive.Fallback
    ref={ref}
    className={cn(
      "flex h-full w-full items-center justify-center rounded-full bg-muted",
      className
    )}
    {...props}
  />
))
AvatarFallback.displayName = AvatarPrimitive.Fallback.displayName

export { Avatar, AvatarImage, AvatarFallback }

client/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 100%;

  --foreground: 240 10% 15%;

  --border: 240 6% 88%;

  --card: 0 0% 99%;

  --card-foreground: 240 10% 15%;

  --card-border: 240 5% 93%;

  --sidebar: 0 0% 96%;

  --sidebar-foreground: 240 10% 15%;

  --sidebar-border: 240 4% 90%;

  --sidebar-primary: 250 60% 60%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 240 8% 92%;

  --sidebar-accent-foreground: 240 10% 20%;

  --sidebar-ring: 250 60% 60%;

  --popover: 0 0% 94%;

  --popover-foreground: 240 10% 15%;

  --popover-border: 240 4% 88%;

  --primary: 250 60% 55%;

  --primary-foreground: 0 0% 100%;

  --secondary: 240 6% 90%;

  --secondary-foreground: 240 10% 20%;

  --muted: 240 8% 92%;

  --muted-foreground: 240 8% 35%;

  --accent: 240 10% 90%;

  --accent-foreground: 240 10% 20%;

  --destructive: 0 72% 51%;

  --destructive-foreground: 0 0% 100%;

  --input: 240 8% 75%;
  --ring: 250 60% 60%;
  --chart-1: 250 60% 50%;
  --chart-2: 165 70% 35%;
  --chart-3: 340 75% 55%;
  --chart-4: 195 80% 45%;
  --chart-5: 280 65% 50%;

  --font-sans: 'Cairo', 'Segoe UI', Tahoma, sans-serif;

  --gradient-primary: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  --gradient-success: linear-gradient(135deg, #11998e 0%, #38ef7d 100%);
  --gradient-warning: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
  --gradient-info: linear-gradient(135deg, #4facfe 0%, #00f2fe 100%);
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .5rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(240 10% 15% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(240 10% 15% / 0.08);
  --shadow-sm: 0px 2px 4px 0px hsl(240 10% 15% / 0.06), 0px 1px 2px -1px hsl(240 10% 15% / 0.04);
  --shadow: 0px 4px 6px -1px hsl(240 10% 15% / 0.08), 0px 2px 4px -2px hsl(240 10% 15% / 0.06);
  --shadow-md: 0px 6px 12px -2px hsl(240 10% 15% / 0.10), 0px 3px 6px -3px hsl(240 10% 15% / 0.08);
  --shadow-lg: 0px 10px 20px -3px hsl(240 10% 15% / 0.12), 0px 4px 8px -4px hsl(240 10% 15% / 0.10);
  --shadow-xl: 0px 20px 32px -4px hsl(240 10% 15% / 0.15), 0px 8px 16px -6px hsl(240 10% 15% / 0.12);
  --shadow-2xl: 0px 25px 50px -12px hsl(240 10% 15% / 0.20);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .15);
  --badge-outline: rgba(255,255,255, .08);

  --opaque-button-border-intensity: 8;

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .05);
  --elevate-2: rgba(255,255,255, .10);

  --background: 220 15% 12%;

  --foreground: 0 0% 96%;

  --border: 220 10% 28%;

  --card: 220 15% 16%;

  --card-foreground: 0 0% 96%;

  --card-border: 220 10% 30%;

  --sidebar: 220 15% 14%;

  --sidebar-foreground: 0 0% 92%;

  --sidebar-border: 220 10% 26%;

  --sidebar-primary: 250 60% 58%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 220 15% 22%;

  --sidebar-accent-foreground: 0 0% 96%;

  --sidebar-ring: 250 60% 60%;

  --popover: 220 15% 17%;

  --popover-foreground: 0 0% 96%;

  --popover-border: 220 10% 30%;

  --primary: 250 60% 58%;

  --primary-foreground: 0 0% 100%;

  --secondary: 220 15% 24%;

  --secondary-foreground: 0 0% 92%;

  --muted: 220 15% 22%;

  --muted-foreground: 0 0% 70%;

  --accent: 220 15% 26%;

  --accent-foreground: 0 0% 96%;

  --destructive: 0 65% 55%;

  --destructive-foreground: 0 0% 100%;

  --input: 220 10% 28%;
  --ring: 250 60% 60%;
  --chart-1: 250 60% 60%;
  --chart-2: 165 70% 45%;
  --chart-3: 340 75% 65%;
  --chart-4: 195 80% 55%;
  --chart-5: 280 65% 60%;

  --shadow-2xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.20);
  --shadow-xs: 0px 1px 3px 0px hsl(0 0% 0% / 0.25);
  --shadow-sm: 0px 2px 4px 0px hsl(0 0% 0% / 0.22), 0px 1px 2px -1px hsl(0 0% 0% / 0.15);
  --shadow: 0px 4px 6px -1px hsl(0 0% 0% / 0.28), 0px 2px 4px -2px hsl(0 0% 0% / 0.20);
  --shadow-md: 0px 6px 12px -2px hsl(0 0% 0% / 0.32), 0px 3px 6px -3px hsl(0 0% 0% / 0.24);
  --shadow-lg: 0px 10px 20px -3px hsl(0 0% 0% / 0.36), 0px 4px 8px -4px hsl(0 0% 0% / 0.28);
  --shadow-xl: 0px 20px 32px -4px hsl(0 0% 0% / 0.40), 0px 8px 16px -6px hsl(0 0% 0% / 0.32);
  --shadow-2xl: 0px 25px 50px -12px hsl(0 0% 0% / 0.45);

}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  .glass-effect {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  .glass-card {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.3);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  }

  .dark .glass-card {
    background: rgba(30, 41, 59, 0.85);
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
  }

  .gradient-primary-bg {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  }

  .gradient-success-bg {
    background: linear-gradient(135deg, #11998e 0%, #38ef7d 100%);
  }

  .gradient-warning-bg {
    background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
  }

  .gradient-info-bg {
    background: linear-gradient(135deg, #4facfe 0%, #00f2fe 100%);
  }

  .card-hover-effect {
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .card-hover-effect:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 20px 40px rgba(0,0,0,0.15);
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  .fade-in {
    animation: fadeIn 0.5s ease-out;
  }

  @keyframes pulse-notification {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }

  .notification-pulse {
    animation: pulse-notification 2s infinite;
  }

  .no-default-hover-elevate {}

  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* تنسيقات شريط التمرير المخصصة - تصميم زجاجي محسّن */
  *::-webkit-scrollbar {
    width: 12px;
    height: 12px;
  }

  *::-webkit-scrollbar-track {
    background: rgba(255, 255, 255, 0.08);
    border-radius: 12px;
    margin: 4px;
  }

  *::-webkit-scrollbar-thumb {
    background: linear-gradient(180deg, rgba(102, 126, 234, 0.6) 0%, rgba(118, 75, 162, 0.6) 100%);
    border-radius: 12px;
    border: 2px solid rgba(255, 255, 255, 0.1);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 0 2px 8px rgba(102, 126, 234, 0.3);
  }

  *::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(180deg, rgba(102, 126, 234, 0.8) 0%, rgba(118, 75, 162, 0.8) 100%);
    border: 2px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 4px 12px rgba(102, 126, 234, 0.5);
    transform: scale(1.05);
  }

  *::-webkit-scrollbar-thumb:active {
    background: linear-gradient(180deg, rgba(102, 126, 234, 0.9) 0%, rgba(118, 75, 162, 0.9) 100%);
    box-shadow: 0 6px 16px rgba(102, 126, 234, 0.6);
  }

  /* Firefox */
  * {
    scrollbar-width: thin;
    scrollbar-color: rgba(102, 126, 234, 0.6) rgba(255, 255, 255, 0.08);
  }

  /* تأثير انتقال سلس للتمرير */
  html {
    scroll-behavior: smooth;
  }
}

